CREATE OR REPLACE FORCE VIEW came.vw_coach (codcoach,coddetprog,codcoa,fecha,horini,horfin,descri,codasicoa,codper,nombre) AS
SELECT ASIGCOACH.CODCOACH,ASIGCOACH.CODDETPROG,COACHING.CODCOA,TO_CHAR(COACHING.FECHA,'DD/MM/YYYY') AS FECHA,COACHING.HORINI,COACHING.HORFIN,COACHING.DESCRI,ASIGCOACH.CODASICOA,PERSONA.CODPER, CONCAT(CONCAT(PERSONA.NOMPER,' '),PERSONA.APEPER) AS NOMBRE FROM COACHING INNER JOIN ASIGCOACH ON coaching.codasicoa = ASIGCOACH.CODASICOA  INNER JOIN PERSONA ON persona.codper = ASIGCOACH.CODPER;