CREATE OR REPLACE FORCE VIEW came.vw_pesos_fase (coddetprog,codfase,estfase,nomfase,codconfpes,pesopart,pesocaso,pesotra,pesoparc,pesofinal) AS
SELECT FASE.CODDETPROG,FASE.CODFASE,FASE.ESTFASE,FASE.NOMFASE,CONFIG_PESOS.CODCONFPES,CONFIG_PESOS.PESOPART,CONFIG_PESOS.PESOCASO,CONFIG_PESOS.PESOTRA,CONFIG_PESOS.PESOPARC,CONFIG_PESOS.PESOFINAL FROM FASE INNER JOIN  CONFIG_PESOS ON CONFIG_PESOS.CODFASE = FASE.CODFASE;